    "url": "http://localhost:4000/csp_apps_sam_d5x_e5x/apps/wdt/wdt_timeout/readme.html#setting-up-the-hardware",
    "relUrl": "/apps/wdt/wdt_timeout/readme.html#setting-up-the-hardware"
